import React, { useState } from "react";
import "../css/pages/slideshow.css";
import imageFlecheD from "../assets/FlècheD.png";
import imageFlecheG from "../assets/FlècheG.png";

const Slideshow = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Aller à l'image suivante
  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Revenir à l'image précédente
  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="slideshow">
      {/* Bouton pour revenir à l'image précédente */}
      <button className="prev" onClick={prevSlide}>
        &#10094;
      </button>

      {/* Image actuelle */}
      <img
        src={images[currentIndex]}
        alt={`Slide ${currentIndex + 1}`}
        className="slideshow-image"
      />

      {/* Bouton pour passer à l'image suivante */}
      <button className="next" onClick={nextSlide}>
        &#10095;
      </button>

      {/* Indicateur de la position actuelle dans le carrousel */}
      <div className="slideshow-counter">
        {currentIndex + 1}/{images.length}
      </div>
    </div>
  );
};

export default Slideshow;
