import React from "react";

import LogoFooter from "../assets/FooterDesktop.png";
import LogoFooterSmall from "../assets/FooterMobile.png";

function Footer() {
  return (
    <div className="footer">
      <img
        src={LogoFooter} // Image par défaut (desktop)
        srcSet={`${LogoFooterSmall} 768w, ${LogoFooter} 1024w`} // Images mobile et desktop
        alt="logo bas de page"
        sizes="(max-width: 768px) 100vw, (min-width: 769px) 50vw" // Ajustement des tailles en fonction de l'écran
      />
    </div>
  );
}

export default Footer;
