import React from "react";
import { NavLink } from "react-router-dom"; /*système d'ancre*/

import Logo from "../assets/logoH.png";
import SmallLogo from "../assets/logo_mobile.png";

function Header() {
  const [logo, setLogo] = useState(Logo);

  // Gérer le changement du logo en fonction de la taille de l'écran
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth <= 650) {
        setLogo(SmallLogo); // Logo pour petits écrans
      } else {
        setLogo(Logo); // Logo pour grands écrans
      }
    };

    // Déclenchement au chargement et lors des changements de taille
    window.addEventListener("resize", handleResize);

    // Appel initial pour définir le bon logo en fonction de la taille de l'écran
    handleResize();

    // Nettoyage de l'event listener
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <div className="header">
      <div className="logo">
        <img src={logo} alt="logo kasa" />
      </div>
      <div className="navigation">
        <ul>
          <NavLink
            to="/"
            className={({ isActive }) => (isActive ? "nav-active" : "nav")}
          >
            <li>Accueil</li>
          </NavLink>
          <NavLink
            to="/about"
            className={({ isActive }) => (isActive ? "nav-active" : "nav")}
          >
            <li>A Propos</li>
          </NavLink>
        </ul>
      </div>
    </div>
  );
}

export default Header;
